import{_ as p,o as t,c as e,a as _}from"./app.ec7a89e7.js";const T=JSON.parse('{"title":"\u65F6\u95F4\u7684\u670B\u53CB2023\u2014\u7B14\u5F55","description":"","frontmatter":{"title":"\u65F6\u95F4\u7684\u670B\u53CB2023\u2014\u7B14\u5F55","date":"2023-01-01T00:00:00.000Z","tags":["life"]},"headers":[],"relativePath":"posts/friends_of_time_2023.md"}'),s={name:"posts/friends_of_time_2023.md"},o=_("<p>\u300C\u6C14\u5019\u8BA9\u8461\u8404\u7565\u5FAE\u9178\u6DA9\uFF0C\u8FD9\u4E00\u676F\uFF0C\u5171\u656C\u8FD9\u4E00\u5E74\u7684\u4E0D\u5B8C\u7F8E\u3002\u300D\u2014\u2014 \u8212\u5BB8</p><p>\u300C\u4ECA\u5E74\u8783\u87F9\u867D\u5C0F\uFF0C\u4EA6\u662F\u4E00\u671F\u4E00\u4F1A\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5148\u4EE5\u81EA\u5DF1\u4E3A\u9053\u8DEF\uFF0C\u518D\u4E3A\u540E\u6765\u8005\u5F00\u8DEF\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u6301\u5FD7\u5982\u5FC3\u75DB\u3002\u300D\u2014\u2014 \u738B\u9633\u660E</p><p>\u300C\u4E0D\u8981\u88AB\u56F0\u96BE\u5B9A\u4E49\uFF0C\u5E26\u7740\u5E0C\u671B\u8BF4\u5B9E\u8BDD\uFF0C\u5E26\u7740\u5B9E\u8DF5\u8BB2\u9053\u7406\uFF0C\u5E26\u7740\u75DB\u5904\u6765\u575A\u6301\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u4EFB\u4F55\u4EBA\u751F\u6545\u4E8B\uFF0C\u90FD\u6709\u4E24\u4E2A\u7248\u672C\u3002\u300D\u2014\u2014 \u51C9\u5B50</p><p>\u300C\u4EBA\u751F\u7ECF\u5386\u5168\u6570\u6253\u5305\uFF0C\u6362\u4E2A\u6218\u573A\u8FD8\u80FD\u5F00\u5F20\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u8FC7\u53BB\u8861\u91CF\u4E00\u4E2A\u4EBA\u9760\u7684\u662F\u793E\u4F1A\u4EF7\u503C\uFF0C\u800C\u73B0\u5728\u91CD\u8981\u7684\u4E0D\u662F\u8EAB\u4EFD\uFF0C\u800C\u662F\u5185\u6838\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u6211\u4E0D\u662F\u5F88\u591A\uFF0C\u4F46\u6211\u662F\u6211\u81EA\u5DF1\u7684\u5168\u90E8\u3002\u300D\u2014\u2014 \u83F2\u5229\u666E\xB7K\xB7\u8FEA\u514B</p><p>\u300C\u4F60\u82B1\u94B1\u4E70\u5230\u7684\uFF0C\u662F\u6211\u54C1\u8FC7\u7684\u6BCF\u4E00\u676F\u5496\u5561\uFF0C\u5403\u8FC7\u7684\u6BCF\u4E00\u9910\u7F8E\u98DF\uFF0C\u8BFB\u8FC7\u7684\u6BCF\u4E00\u672C\u4E66\uFF0C\u8C08\u8FC7\u7684\u6BCF\u4E00\u6B21\u604B\u7231\uFF0C\u53BB\u8FC7\u7684\u6BCF\u4E00\u4E2A\u5730\u65B9\u3002\u300D\u2014\u2014 \u5854\u897F\u59C6\xB7\u8F9B</p><p>\u300C\u6211\u4EEC\u8EAB\u4E0A\u6700\u6709\u4EF7\u503C\u7684\u4E1C\u897F\uFF0C\u4E0D\u662F\u8BC1\u4E66\u548C\u6280\u80FD\uFF0C\u800C\u662F\u8FC7\u53BB\u4E00\u5207\u7ECF\u5386\u7684\u603B\u548C\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u4E00\u6735\u73AB\u7470\u6B63\u9A6C\u4E0D\u505C\u8E44\u5730\u6210\u4E3A\u53E6\u4E00\u6735\u73AB\u7470\uFF0C\u4F60\u662F\u4E91\uFF0C\u662F\u6D77\uFF0C\u662F\u5FD8\u5374\uFF0C\u4F60\u4E5F\u662F\u4F60\u66FE\u7ECF\u5931\u53BB\u7684\u6BCF\u4E00\u4E2A\u4F60\u3002\u300D\u2014\u2014 \u535A\u5C14\u8D6B\u65AF</p><p>\u300C\u9732\u8425\uFF0C\u5176\u5B9E\u5C31\u662F\u6210\u5E74\u4EBA\u7684\u8FC7\u5BB6\u5BB6\u3002\u300D\u2014\u2014 \u8D75\u5F84\u6587</p><p>\u300C\u629B\u53BB\u8EAB\u4EFD\u548C\u793E\u4F1A\u4EF7\u503C\uFF0C\u4F60\u662F\u8C01\uFF1F\u6C38\u8FDC\u4FDD\u6709\u4ECE\u5185\u6838\u51FA\u53D1\u7684\u80FD\u529B\u3002\u6311\u6218\uFF0C\u52AA\u529B\uFF0C\u6210\u5C31\uFF0C\u8FD9\u4E2A\u94C1\u4E09\u89D2\u5C31\u5728\uFF0C\u6211\u5C31\u5728\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u795D\u6BCF\u4E00\u4E2A\u4EBA\uFF0C\u90FD\u80FD\u7528\u81EA\u5DF1\u7684\u7980\u8D4B\u3001\u504F\u597D\u548C\u8FD0\u6C14\uFF0C\u5728\u81EA\u5DF1\u7684\u5934\u9876\u642D\u5EFA\u51FA\u4E00\u6240\u53EF\u80FD\u4E0D\u5B8C\u7F8E\uFF0C\u4F46\u53EA\u5C5E\u4E8E\u81EA\u5DF1\u7684\u5929\u7A7A\u4E4B\u57CE\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u7F8E\u662F\u4EFB\u4F55\u4E00\u4E2A\u60F3\u8981\u6253\u5F00\u76F8\u673A\u7684\u65F6\u523B\uFF1B\u7F8E\u662F\u7535\u5F71\u9662\u6F06\u9ED1\u4E2D\u4ED6\u8F7B\u8F7B\u78B0\u5230\u4E86\u5979\u7684\u80A9\u8180\uFF1B\u7F8E\u662F\u300E\u8FD9\u7248\u65B9\u6848\u6211\u4EECOK\u300F\uFF1B\u7F8E\u662F\u8BF4\u597D\u6700\u540E\u4E00\u676F\uFF0C\u4F46\u4E3A\u4E86\u518D\u591A\u5750\u4E00\u4F1A\u53C8\u5FCD\u4E0D\u4F4F\u5012\u6EE1\u7684\u9152\uFF1B\u7F8E\u662F\u8001\u677F\u7684\u53CD\u4E49\u8BCD\u2026\u300D\u2014\u2014 GQ\u5B9E\u9A8C\u5BA4</p><p>\u300C\u4EE5\u4E00\u822C\u4EBA\u7684\u52AA\u529B\u7A0B\u5EA6\u4E4B\u4F4E\uFF0C\u6839\u672C\u8F6E\u4E0D\u5230\u62FC\u5929\u8D4B\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5185\u5377\u662F\u4F4E\u6C34\u5E73\u7684\u590D\u6742\u5316\uFF0C\u5927\u5BB6\u4E92\u76F8\u8017\u7740\uFF0C\u300E\u5FAE\u96D5\u300F\u4E0D\u662F\uFF0C\u8FD9\u4E48\u5C0F\u7684\u5730\u65B9\uFF0C\u627E\u5230\u65E0\u7A77\u591A\u4E0B\u5200\u7684\u89D2\u5EA6\u3002\u5728\u5185\u5377\u7684\u5C3D\u5934\uFF0C\u8BF7\u8BD5\u8BD5\u300E\u5FAE\u96D5\u300F\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u6240\u6709\u7684\u826F\u597D\u5173\u7CFB\u90FD\u5EFA\u7ACB\u5728\u4FE1\u4EFB\u4E0A\uFF0C\u6240\u6709\u7684\u65B0\u4EBA\u90FD\u5EFA\u7ACB\u5728\u771F\u5B9E\u89E6\u70B9\u4E0A\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u7A7A\u4E2D\u5C0F\u59D0\u7684\u7B11\u5BB9\uFF0C\u4E0E\u98DE\u884C\u5B89\u5168\u7684\u7EDF\u8BA1\u516C\u62A5\u540C\u7B49\u91CD\u8981\u3002\u300D\u2014\u2014 \u5409\u767B\u65AF</p><p>\u300C\u521B\u65B0\u7684\u7A7A\u95F4\uFF0C\u5C31\u5728\u811A\u4E0B\uFF0C\u539F\u5730\u4E0D\u52A8\u4E5F\u53EF\u4EE5\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5546\u4E1A\u6F14\u5316\u7684\u4E00\u4E2A\u57FA\u672C\u89C4\u5F8B\uFF1A\u628A\u66F4\u4E30\u5BCC\u7684\u4E1C\u897F\uFF0C\u4EE5\u66F4\u4FBF\u5B9C\u7684\u4EF7\u683C\uFF0C\u7528\u66F4\u65B9\u4FBF\u7684\u65B9\u5F0F\uFF0C\u4EA4\u5230\u7528\u6237\u624B\u4E2D\u3002\u300D\u2014\u2014 \u5218\u6DA6</p><p>\u300C\u8FC7\u53BB\u4E09\u5E74\uFF0C\u5F88\u591A\u4EBA\u8FC7\u7684\u4E0D\u5BB9\u6613\uFF0C\u5F88\u591A\u4EBA\u8BF4\u4E8B\u513F\u6CA1\u90A3\u4E48\u5BB9\u6613\u505A\u4E86\uFF0C\u673A\u4F1A\u597D\u50CF\u6CA1\u90A3\u4E48\u591A\u4E86\uFF0C\u4F46\u53CD\u8FC7\u6765\u4E00\u60F3\uFF0C\u8FD9\u4E0D\u5C31\u662F\u628A\u6CE1\u6CAB\u6324\u5E72\u4E86\u4E48\uFF0C\u90A3\u4E9B\u9760\u7740\u8D22\u5927\u6C14\u7C97\u5C31\u53EF\u4EE5\u505A\u4E8B\u7684\u4EBA\uFF0C\u73B0\u5728\u6CA1\u620F\u5531\u4E86\uFF0C\u8C01\u6765\u5531\u620F\uFF1F\u7559\u4E0B\u6765\u7684\u90FD\u662F\u4E00\u4E9B\u975E\u5E38\u89C4\u5F8B\u548C\u6734\u7D20\u7684\u5E38\u8BC6\uFF0C\u56DE\u5230\u57FA\u672C\u7684\u8981\u7D20\uFF0C\u6301\u7EED\u5FAE\u96D5\uFF0C\u662F\u4E0D\u662F\u4E5F\u80FD\u5E72\u6210\u4E00\u4EF6\u4E86\u4E0D\u8D77\u7684\u4E8B\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5982\u679C\u6253\u7B97\u5728\u67D0\u4E2A\u884C\u4E1A\u6DF1\u8015\u4E0B\u53BB\uFF0C\u4E0D\u4EC5\u8981\u5728\u5927\u5BB6\u770B\u7684\u5730\u65B9\u4E0B\u624B\uFF0C\u4E5F\u8981\u5728\u522B\u4EBA\u770B\u4E0D\u89C1\u7684\u5730\u65B9\u4E0B\u624B\u3002\u4E00\u4E2A\u574F\u6D88\u606F\uFF0C\u4E00\u4E2A\u597D\u6D88\u606F\u4F60\u8981\u542C\u54EA\u4E2A\uFF1F\u574F\u6D88\u606F\u662F\u8FD9\u4E2A\u5730\u65B9\u4E0D\u592A\u597D\u627E\uFF0C\u597D\u6D88\u606F\u662F\u8FD9\u4E2A\u5730\u65B9\u5B83\u6709\u7684\u662F~~\u8FD9\u4EF6\u4E8B\u6CA1\u5C3D\u5934\uFF0C\u6C38\u8FDC\u6709\u4E0D\u65AD\u4F18\u5316\u4E0B\u53BB\u7684\u7A7A\u95F4\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u4E2D\u56FD\u5C31\u50CF\u4E00\u53E3\u5927\u706B\u9505\uFF0C\u91CC\u9762\u5143\u7D20\u8D8A\u4E30\u5BCC\uFF0C\u5473\u9053\u5C31\u8D8A\u597D\uFF0C\u521B\u9020\u6027\u5C31\u8D8A\u4E30\u5BCC\u3002\u300D\u2014\u2014 \u66FE\u822A</p><p>\u300C\u6CA1\u6709\u4EBA\u662F\u4E00\u5EA7\u5B64\u5C9B\uFF0C\u53EF\u4EE5\u81EA\u5168\u3002\u300D\u2014\u2014 \u7EA6\u7FF0\xB7\u591A\u6069</p><p>\u300C\u9644\u8FD1\u7684\u8001\u767E\u59D3\u9700\u8981\u4EC0\u4E48\uFF0C\u6211\u5C31\u6EE1\u8DB3\u4ED6\u4EEC\u4EC0\u4E48\u3002\u300D\u2014\u2014 \u6C6A\u56FD\u7389</p><p>\u300C\u661F\u8FB0\u5927\u6D77\uFF0C\u5C31\u5728\u6211\u7684\u9644\u8FD1\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5168\u8FC7\u7A0B\u6DF1\u5EA6\u6D78\u6DA6\u3002\u300D\u2014\u2014 \u6885\u8D50\u742A</p><p>\u300C\u521B\u5EFA\u5B66\u6821\u7684\u76EE\u7684\uFF0C\u662F\u5C06\u5386\u53F2\u4E0A\u4EBA\u7C7B\u7684\u7CBE\u795E\u5185\u6DB5\u8F6C\u5316\u4E3A\u5F53\u4E0B\u751F\u673A\u52C3\u52C3\u7684\u7CBE\u795E\uFF0C\u7531\u6B64\u5F15\u5BFC\u5B66\u751F\u638C\u63E1\u5FC5\u8981\u7684\u77E5\u8BC6\u548C\u6280\u80FD\u3002\u300D\u2014\u2014 \u96C5\u601D\u8D1D\u5C14\u65AF</p><p>\u300C\u559C\u6B22\u4E0A\u90A3\u54EA\u95E8\u8BFE\u5F88\u53EF\u80FD\u662F\u56E0\u4E3A\u559C\u6B22\u90A3\u4E2A\u8001\u5E08\uFF0C\u60F3\u56DE\u5B66\u6821\u770B\u770B\u5F88\u53EF\u80FD\u662F\u60F3\u56DE\u53BB\u770B\u770B\u67D0\u4E2A\u4EBA\uFF0C\u7ECF\u8425\u661F\u8FB0\u5927\u6D77\uFF0C\u5165\u624B\u70B9\u4E0D\u5728\u8FDC\u65B9\uFF0C\u800C\u662F\u773C\u524D\u7684\u3001\u5177\u4F53\u7684\u4EBA\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5DF2\u8BC6\u4E7E\u5764\u5927\uFF0C\u72B9\u601C\u8349\u6728\u9752\u3002\u300D\u2014\u2014 \u9A6C\u4E00\u6D6E</p><p>\u300C\u6570\u5B57\u5316\u6B63\u5728\u6539\u53D8\u4E2D\u56FD\u5546\u4E1A\u7684\u5E95\u5C42\u571F\u58E4\u3002\u300D\u2014\u2014 \u51AF\u5927\u521A</p><p>\u300C\u54EA\u91CC\u6570\u5B57\u5316\u7A0B\u5EA6\u9AD8\uFF0C\u5C31\u5F80\u54EA\u91CC\u8FC1\u5F99\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u4E2D\u56FD\u6B63\u5D1B\u8D77\u4E00\u6279\u56FD\u6C11\u7EA7\u5546\u4E1A\u5E94\u7528\u3002\u300D\u2014\u2014 \u4F55\u521A</p><p>\u300C\u6570\u5B57\u5316\u5BFC\u81F4\u6210\u89D2\u513F\u4E4B\u540E\uFF0C\u8FD8\u5F97\u63A5\u7740\u300E\u6328\u6253\u300F\u3002\u7FA1\u6155\uFF0C\u4F46\u4E0D\u5AC9\u5992\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u9E3F\u9E44\u7109\u77E5\u71D5\u96C0\u4E4B\u5FD7\u3002\u300D\u2014\u2014 \u4F55\u5E06</p><p>\u300C\u77F3\u4EE4\u4EBA\u53E4\uFF0C\u6C34\u4EE4\u4EBA\u8FDC\u3002\u300D\u2014\u2014 \u6587\u9707\u4EA8</p><p>\u300C\u5BF9\u4E16\u754C\u91CD\u65B0\u7F16\u7801\u7684\u80FD\u529B\uFF0C\u5C31\u662F\u6700\u4F4E\u6210\u672C\u7684\u521B\u65B0\u8D44\u6E90\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u6211\u4EEC\u505A\u7684\u4E8B\u4E5F\u8BB8\u65E0\u6CD5\u9009\u62E9\uFF0C\u4F46\u662F\u6211\u4EEC\u5BF9\u63A5\u7684\u4F20\u7EDF\u53EF\u4EE5\u9009\u62E9\uFF0C\u521B\u65B0\u5C31\u5728\u5176\u4E2D\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u770B\u5F97\u89C1\u591A\u8FDC\u7684\u8FC7\u53BB\uFF0C\u5C31\u80FD\u770B\u5230\u591A\u8FDC\u7684\u672A\u6765\u3002\u300D\u2014\u2014 \u4E18\u5409\u5C14</p><p>\u300C\u4F5C\u54C1\uFF1A\u5BF9\u4E16\u754C\u91CD\u65B0\u7F16\u7801\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u50CF\u75AF\u72D7\u4E00\u6837\u53BB\u5360\u6709\uFF0C\u628A\u4E16\u754C\u636E\u4E3A\u5DF1\u6709\u7684\u65B9\u6CD5\u53EA\u6709\u4E00\u79CD\uFF0C\u5C31\u662F\u628A\u5B83\u4EEC\u53D8\u6210\u81EA\u5DF1\u7684\u521B\u4F5C\u3002\u300D\u2014\u2014 \u674E\u8BDE</p><p>\u300C\u8BF7\u8086\u610F\u6325\u6BEB\uFF0C\u522B\u5FD8\u4E86\u843D\u6B3E\u76D6\u7AE0\uFF0C\u8FD9\u5C31\u662F\u6700\u4FBF\u5B9C\u7684\u521B\u65B0\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u9009\u62E9\u821E\u8E48\uFF0C\u800C\u4E0D\u662F\u5954\u8DD1\u3002\u300D\u2014\u2014 \u5C11\u6960</p><p>\u300C\u7F8E\u61C2\u5F97\u8BF4\uFF1A\u591F\u4E86\u3002\u800C\u91CE\u86EE\u5435\u7740\u95F9\u7740\u8FD8\u8981\u66F4\u591A\u3002\u300D\u2014\u2014 \u6CF0\u6208\u5C14</p><p>\u300C\u8981\u7D20\uFF1A\u6CD5\u5EA6\u8C28\u4E25\uFF0C\u53D8\u5316\u51FA\u5947\u3002\u8981\u6709\u89C4\u77E9\u3001\u6709\u7EA6\u675F\u3001\u6709\u8FB9\u754C\uFF0C\u9664\u6B64\u4E4B\u5916\u653E\u5F00\u521B\u65B0\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u8BF7\u5927\u5BB6\u611F\u53D7\u4E00\u4E0B\u8981\u7D20\u6CD5\u548C\u4EE5\u524D\u7684\u5B66\u5148\u8FDB\u6CD5\u6709\u4EC0\u4E48\u4E0D\u4E00\u6837\uFF0C\u539F\u6765\u6211\u4EEC\u90FD\u662F\u89C9\u5F97\u81EA\u5DF1\u4E0D\u884C\uFF0C\u90A3\u4E2A\u6807\u6746\u3001\u699C\u6837\u884C\uFF0C\u6240\u4EE5\u6211\u5426\u5B9A\u81EA\u5DF1\uFF0C\u5411\u522B\u4EBA\u5B66\u4E60\uFF0C\u53EF\u662F\u8981\u7D20\u6CD5\u4E0D\u662F\uFF0C\u8981\u7D20\u6CD5\u662F\u8FFD\u95EE\u81EA\u5DF1\uFF1A\u6211\u4E3A\u4EC0\u4E48\u80FD\u5230\u4ECA\u5929\uFF0C\u662F\u4EC0\u4E48\u4E1C\u897F\u8BA9\u6211\u6709\u76EE\u524D\u7684\u6210\u5C31\u3002\u628A\u81EA\u5DF1\u7684\u6210\u529F\u7ECF\u9A8C\u603B\u7ED3\u51FA\u6765\uFF0C\u4F5C\u4E3A\u8981\u7D20\u56FA\u5B9A\u4E0D\u53D8\uFF0C\u7136\u540E\u5269\u4E0B\u7684\u968F\u4FBF\u53D8\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u5982\u679C\u6682\u65F6\u770B\u4E0D\u6E05\u672A\u6765\uFF0C\u4E0D\u77E5\u9053\u4EC0\u4E48\u662F\u597D\u7684\uFF0C\u4E5F\u4E0D\u77E5\u9053\u5411\u8C01\u5B66\u4E60\uFF0C\u8FD9\u662F2022\u5E74\u7ECF\u5E38\u9047\u5230\u7684\u60C5\u7EEA\uFF0C\u6CA1\u5173\u7CFB\uFF0C\u8C03\u52A8\u8981\u7D20\u6CD5\uFF0C\u8FD9\u5C31\u662F\u821E\u8E48\u3002\u6211\u4EEC\u4E0D\u53EA\u6709\u5954\u8DD1\uFF0C\u8FD8\u53EF\u4EE5\u821E\u8E48\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u7ECF\u8425\u597D\u81EA\u5DF1\u7684\u73B0\u5728\uFF0C\u7B49\u5F85\u672A\u6765\u5411\u6211\u98DE\u5954\u800C\u6765\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u672A\u6765\uFF0C\u662F\u73B0\u5728\u67D0\u4E2A\u56E0\u7D20\u7684\u5C55\u5F00\u3002\u4ECE\u8FD9\u4E2A\u610F\u4E49\u4E0A\u8BB2\uFF0C\u73B0\u5728\uFF0C\u5C31\u662F\u672A\u6765\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u62D2\u7EDD\u540D\u8BCD\u7684\u8BF1\u60D1\uFF0C\u6210\u4E3A\u4E00\u4E2A\u52A8\u8BCD\u3002\u300D\u2014\u2014 \u8D75\u6C40\u9633</p><p>\u300C\u5728\u505A\u9009\u62E9\u7684\u65F6\u5019\uFF0C\u66F4\u52A0\u5173\u6CE8\u81EA\u5DF1\u7684\u5185\u6838\uFF0C\u800C\u4E0D\u662F\u62E5\u6709\u7684\u6807\u7B7E\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u628A\u4E00\u5207\u90FD\u770B\u6210\u753B\u5E03\u3001\u7434\u8C31\u548C\u821E\u53F0\uFF0C\u6709\u80C6\u6C14\u628A\u4E16\u754C\u636E\u4E3A\u5DF1\u6709\u3002\u300D\u2014\u2014 \u7F57\u80D6</p><p>\u300C\u6211\u6CA1\u6709\u5931\u8D25\u8FC7\uFF0C\u8981\u4E48\u8D62\u5F97\u80DC\u5229\uFF0C\u8981\u4E48\u5B66\u5230\u4E1C\u897F\u3002\u300D\u2014\u2014 \u66FC\u5FB7\u62C9</p>",55),a=[o];function r(n,i,c,d,f,m){return t(),e("div",null,a)}const h=p(s,[["render",r]]);export{T as __pageData,h as default};
