import{_ as e,o as r,c as a,a as t}from"./app.ec7a89e7.js";const m=JSON.parse('{"title":"Web\u6E32\u67D3\u7684\u6743\u8861","description":"","frontmatter":{"title":"Web\u6E32\u67D3\u7684\u6743\u8861","date":"2021-12-13T17:08:08.000Z","tags":["chrome","performance"]},"headers":[{"level":2,"title":"\u672F\u8BED","slug":"\u672F\u8BED","link":"#\u672F\u8BED","children":[{"level":3,"title":"\u6E32\u67D3","slug":"\u6E32\u67D3","link":"#\u6E32\u67D3","children":[]},{"level":3,"title":"\u8868\u73B0","slug":"\u8868\u73B0","link":"#\u8868\u73B0","children":[]}]},{"level":2,"title":"\u670D\u52A1\u5668\u6E32\u67D3","slug":"\u670D\u52A1\u5668\u6E32\u67D3","link":"#\u670D\u52A1\u5668\u6E32\u67D3","children":[]},{"level":2,"title":"\u9759\u6001\u6E32\u67D3","slug":"\u9759\u6001\u6E32\u67D3","link":"#\u9759\u6001\u6E32\u67D3","children":[]},{"level":2,"title":"\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3","slug":"\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3","link":"#\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3","children":[]},{"level":2,"title":"\u5BA2\u6237\u7AEF\u6E32\u67D3","slug":"\u5BA2\u6237\u7AEF\u6E32\u67D3","link":"#\u5BA2\u6237\u7AEF\u6E32\u67D3","children":[]},{"level":2,"title":"rehydration\u4E0B\u7684SSR\u548CCSR","slug":"rehydration\u4E0B\u7684ssr\u548Ccsr","link":"#rehydration\u4E0B\u7684ssr\u548Ccsr","children":[]},{"level":2,"title":"\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784","slug":"\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784","link":"#\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784","children":[]},{"level":2,"title":"\u4E09\u4F53\u6E32\u67D3","slug":"\u4E09\u4F53\u6E32\u67D3","link":"#\u4E09\u4F53\u6E32\u67D3","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]},{"level":2,"title":"\u53C2\u8003\u8D44\u6599","slug":"\u53C2\u8003\u8D44\u6599","link":"#\u53C2\u8003\u8D44\u6599","children":[]}],"relativePath":"posts/render_on_the_web.md"}'),i={name:"posts/render_on_the_web.md"},l=t('<nav class="table-of-contents"><ul><li><a href="#\u672F\u8BED">\u672F\u8BED</a><ul><li><a href="#\u6E32\u67D3">\u6E32\u67D3</a></li><li><a href="#\u8868\u73B0">\u8868\u73B0</a></li></ul></li><li><a href="#\u670D\u52A1\u5668\u6E32\u67D3">\u670D\u52A1\u5668\u6E32\u67D3</a></li><li><a href="#\u9759\u6001\u6E32\u67D3">\u9759\u6001\u6E32\u67D3</a></li><li><a href="#\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3">\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3</a></li><li><a href="#\u5BA2\u6237\u7AEF\u6E32\u67D3">\u5BA2\u6237\u7AEF\u6E32\u67D3</a></li><li><a href="#rehydration\u4E0B\u7684ssr\u548Ccsr">rehydration\u4E0B\u7684SSR\u548CCSR</a></li><li><a href="#\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784">\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784</a></li><li><a href="#\u4E09\u4F53\u6E32\u67D3">\u4E09\u4F53\u6E32\u67D3</a></li><li><a href="#\u603B\u7ED3">\u603B\u7ED3</a></li><li><a href="#\u53C2\u8003\u8D44\u6599">\u53C2\u8003\u8D44\u6599</a></li></ul></nav><p>\u6784\u5EFA\u7F51\u7AD9\u7684\u65B9\u6CD5\u6709\u5F88\u591A\uFF0C\u4F46\u662F\u6BCF\u79CD\u6784\u5EFA\u90FD\u79BB\u4E0D\u5F00\u6E32\u67D3\uFF0C\u90A3\u5982\u4F55\u4ECE\u6027\u80FD\u7684\u89D2\u5EA6\u6765\u6743\u8861Web\u4E0A\u7684\u6E32\u67D3\u5462\uFF1F</p><h2 id="\u672F\u8BED" tabindex="-1">\u672F\u8BED <a class="header-anchor" href="#\u672F\u8BED" aria-hidden="true">#</a></h2><h3 id="\u6E32\u67D3" tabindex="-1">\u6E32\u67D3 <a class="header-anchor" href="#\u6E32\u67D3" aria-hidden="true">#</a></h3><ul><li><strong>SSR</strong>\uFF1A\u670D\u52A1\u5668\u6E32\u67D3\uFF0C\u5728\u670D\u52A1\u5668\u4E0A\u5C06\u5BA2\u6237\u7AEF\u6216\u901A\u7528\u7A0B\u5E8F\u6E32\u67D3\u4E3A HTML\u3002</li><li><strong>CSR</strong>\uFF1A\u5BA2\u6237\u7AEF\u6E32\u67D3\uFF0C \u5728\u6D4F\u89C8\u5668\u4E2D\u6E32\u67D3\u7A0B\u5E8F\uFF0C\u901A\u5E38\u4F7F\u7528 DOM\u3002</li><li><strong>Rehydration</strong>\uFF1A\u540C\u6784\u6E32\u67D3\uFF0C\u590D\u7528\u670D\u52A1\u5668\u6E32\u67D3\u7684 HTML \u7684 DOM \u6811\u548C\u6570\u636E\u540E\uFF0C\u5728\u5BA2\u6237\u7AEF\u518D\u8FDB\u884C\u6E32\u67D3\u3002</li><li><strong>Prerendering</strong>\uFF1A\u9884\u6E32\u67D3\uFF0C\u5728\u6784\u5EFA\u65F6\u8FD0\u884C\u5BA2\u6237\u7AEF\u7A0B\u5E8F\u4EE5\u5C06\u5176\u521D\u59CB\u72B6\u6001\u6355\u83B7\u4E3A\u9759\u6001 HTML\u3002</li></ul><h3 id="\u8868\u73B0" tabindex="-1">\u8868\u73B0 <a class="header-anchor" href="#\u8868\u73B0" aria-hidden="true">#</a></h3><ul><li><strong>TTFB</strong>\uFF1A\u7B2C\u4E00\u4E2A\u5B57\u8282\u51FA\u73B0\u7684\u65F6\u95F4\uFF0C\u88AB\u770B\u4F5C\u5355\u51FB\u94FE\u63A5\u548C\u7B2C\u4E00\u6B21\u5185\u5BB9\u8FDB\u5165\u7684\u65F6\u95F4\u3002</li><li><strong>FP</strong>\uFF1AFirst Paint\uFF0C\u50CF\u7D20\u7B2C\u4E00\u6B21\u53EF\u89C1\u3002</li><li><strong>FCP</strong>\uFF1AFirst Contentful Paint\uFF0C\u8BF7\u6C42\u7684\u5185\u5BB9\uFF08\u6BD4\u5982\u6B63\u6587\uFF09\u7B2C\u4E00\u6B21\u53EF\u89C1\u3002</li><li><strong>TTI</strong>\uFF1ATime To Interactive\uFF0C\u9875\u9762\u53D8\u4E3A\u53EF\u4EA4\u4E92\u7684\u65F6\u95F4\uFF08\u4E8B\u4EF6\u8FDE\u63A5\u7B49\uFF09\u3002</li></ul><h2 id="\u670D\u52A1\u5668\u6E32\u67D3" tabindex="-1">\u670D\u52A1\u5668\u6E32\u67D3 <a class="header-anchor" href="#\u670D\u52A1\u5668\u6E32\u67D3" aria-hidden="true">#</a></h2><p><em>\u670D\u52A1\u5668\u6E32\u67D3\u4E3A\u670D\u52A1\u5668\u4E0A\u7684\u9875\u9762\u751F\u6210\u5B8C\u6574\u7684 HTML \u4EE5\u54CD\u5E94<a href="https://juejin.cn/post/7039036362653171742#heading-10" target="_blank" rel="noreferrer">\u5BFC\u822A</a>\u3002\u8FD9\u907F\u514D\u4E86\u5728\u5BA2\u6237\u7AEF\u4E0A\u8FDB\u884C\u6570\u636E\u83B7\u53D6\u548C\u6A21\u677F\u5316 \u7684\u989D\u5916\u5F00\u9500\uFF0C\u56E0\u4E3A\u5B83\u662F\u5728\u6D4F\u89C8\u5668\u83B7\u5F97\u54CD\u5E94\u4E4B\u524D\u5904\u7406\u7684\u3002</em></p><p>\u670D\u52A1\u5668\u6E32\u67D3\u4F1A\u4EA7\u751F\u6BD4\u8F83\u5FEB\u7684 <code>\u9996\u6B21\u7ED8\u5236(FP)</code> \u548C <code>\u9996\u6B21\u5185\u5BB9\u7ED8\u5236(FCP)</code>\u3002\u5728\u670D\u52A1\u5668\u4E0A\u8FD0\u884C\u9875\u9762\u903B\u8F91\u548C\u6E32\u67D3\u53EF\u4EE5\u907F\u514D\u5411\u5BA2\u6237\u7AEF\u53D1\u9001\u5927\u91CF JavaScript\uFF0C\u8FD9\u6709\u52A9\u4E8E\u5B9E\u73B0\u5FEB\u901F\u7684 <code>\u4EA4\u4E92\u65F6\u95F4(TTI)</code>\u3002\u56E0\u4E3A\u901A\u8FC7\u670D\u52A1\u5668\u6E32\u67D3\uFF0C\u5B9E\u9645\u4E0A\u53EA\u662F\u5C06\u6587\u672C\u548C\u94FE\u63A5\u53D1\u9001\u5230\u7528\u6237\u7684\u6D4F\u89C8\u5668\u3002\u8FD9\u79CD\u65B9\u6CD5\u53EF\u4EE5\u5F88\u597D\u5730\u9002\u7528\u4E8E\u5404\u79CD\u8BBE\u5907\u548C\u7F51\u7EDC\u6761\u4EF6\uFF0C\u5E76\u5F00\u542F\u4E86\u6D4F\u89C8\u5668\u4F18\u5316\uFF0C\u4F8B\u5982\u6D41\u5F0F\u6587\u6863\u89E3\u6790\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213145054.png" alt=""></p><p>\u670D\u52A1\u5668\u6E32\u67D3\u7684\u8BDD\uFF0C\u7528\u6237\u4E0D\u592A\u53EF\u80FD\u5728\u8BBF\u95EE\u7AD9\u70B9\u524D\u7B49\u5F85 CPU-bound\uFF08\u8BA1\u7B97\u5BC6\u96C6\u578B\uFF09\u5904\u7406JavaScript\u3002\u5373\u4F7F\u5728\u4E09\u65B9JS\u65E0\u6CD5\u907F\u514D\u7684\u60C5\u51B5\u4E0B\uFF0C\u4F7F\u7528\u670D\u52A1\u5668\u6E32\u67D3\u6765\u964D\u4F4E\u81EA\u5DF1\u7684\u5F53\u524DJS\u6210\u672C\uFF0C\u53EF\u4EE5\u6709\u66F4\u591A\u7684\u6027\u80FD\u9884\u7B97\u3002</p><blockquote><ul><li>CPU-bound\uFF1A\u8BA1\u7B97\u5BC6\u96C6\u578B\uFF0C\u8868\u793A\u5728\u591A\u91CD\u7A0B\u5E8F\u7CFB\u7EDF\u4E2D\uFF0C\u5927\u90E8\u4EFD\u65F6\u95F4\u7528\u6765\u505A\u8BA1\u7B97\u3001\u903B\u8F91\u5224\u65AD\u7B49CPU\u52A8\u4F5C\u7684\u7A0B\u5E8F\u3002</li><li>\u6027\u80FD\u9884\u7B97\uFF1A\u4E00\u4E2A\u56E2\u961F\u4E0D\u80FD\u5141\u8BB8\u8D85\u8FC7\u7684\u9875\u9762<strong>\u9650\u5236</strong>\uFF0C<a href="https://juejin.cn/post/6844903700524171271" target="_blank" rel="noreferrer">\u70B9\u51FB\u67E5\u770B\u94FE\u63A5</a></li></ul></blockquote><p>\u4F46\u662F\u8FD9\u79CD\u65B9\u6CD5\u6709\u4E00\u4E2A\u4E3B\u8981\u7F3A\u70B9\uFF1A\u5728\u670D\u52A1\u5668\u4E0A\u751F\u6210\u9875\u9762\u9700\u8981\u7684\u65F6\u95F4\uFF0C\u4F1A\u5BFC\u81F4 <code>\u9996\u5B57\u6BCD\u65F6\u95F4\uFF08TTFB\uFF09</code>\u53D8\u6162\u3002</p><p>\u670D\u52A1\u7AEF\u6E32\u67D3\u80FD\u4E0D\u80FD\u6EE1\u8DB3\u5F53\u524D\u7684\u7CFB\u7EDF\u53D6\u51B3\u4E8E\u6784\u5EFA\u7684\u524D\u7AEF\u4F53\u9A8C\u7C7B\u578B\u3002\u6709\u5173\u670D\u52A1\u5668\u6E32\u67D3\u548C\u5BA2\u6237\u7AEF\u6E32\u67D3\u4E00\u76F4\u5B58\u5728\u7740\u957F\u671F\u7684\u4E89\u8BBA\uFF0C\u4F46\u662F\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u53EF\u4EE5\u5BF9\u67D0\u4E9B\u9875\u9762\u4F7F\u7528\u670D\u52A1\u5668\u6E32\u67D3\u3002</p><p>\u8BB8\u591A\u73B0\u4EE3\u6846\u67B6\u3001\u5E93\u548C\u4F53\u7CFB\u7ED3\u6784\u652F\u6301\u5728\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u8D77\u4E0A\u5448\u73B0\u76F8\u540C\u7684\u7A0B\u5E8F\uFF0C\u8FD9\u4E9B\u6280\u672F\u53EF\u7528\u4E8E\u670D\u52A1\u5668\u6E32\u67D3\uFF0C\u5728\u4F7F\u7528\u7684\u65F6\u5019\uFF0C\u6CE8\u610F\u5B83\u4EEC\u5728\u6E32\u67D3\u53D1\u751F\u5728\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u7AEF\u7684\u65B9\u6848\u7684\u5B9E\u73B0\u3002</p><h2 id="\u9759\u6001\u6E32\u67D3" tabindex="-1">\u9759\u6001\u6E32\u67D3 <a class="header-anchor" href="#\u9759\u6001\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u9759\u6001\u6E32\u67D3\u53D1\u751F\u5728\u6784\u5EFA\u65F6\uFF0C\u5E76\u63D0\u4F9B\u5FEB\u901F\u7684 <code>\u9996\u6B21\u7ED8\u5236\uFF08FP\uFF09</code>\u3001<code>\u9996\u6B21\u5185\u5BB9\u7ED8\u5236\uFF08FCP\uFF09</code> \u548C <code>\u4EA4\u4E92\u65F6\u95F4\uFF08TTI\uFF09</code>\uFF0C\u548C\u670D\u52A1\u5668\u6E32\u67D3\u4E0D\u540C\uFF0C\u4ED6\u8FD8\u4F1A\u5B9E\u73B0\u5FEB\u901F\u7684 <code>\u9996\u6B21\u9996\u5B57\u8282\u65F6\u95F4\uFF08TTFB\uFF09</code>\uFF0C\u56E0\u4E3A\u9875\u9762\u7684HTML\u4E0D\u5FC5\u5373\u65F6\u751F\u6210\u3002\u4E00\u822C\uFF0C\u9759\u6001\u6E32\u67D3\u610F\u5473\u7740\u63D0\u524D\u4E3A\u6BCF\u4E2AURL\u751F\u6210\u5355\u72EC\u7684HTML\u6587\u4EF6\uFF0C\u901A\u8FC7\u63D0\u524D\u751F\u6210HTML\uFF0C\u9759\u6001\u6E32\u67D3\u53EF\u4EE5\u90E8\u7F72\u5230\u591A\u4E2ACDN\uFF0C\u4ECE\u800C\u5229\u7528 <a href="https://www.easemob.com/news/7325" target="_blank" rel="noreferrer">\u8FB9\u7F18\u7F13\u5B58</a>\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213151915.png" alt=""></p><p>\u9759\u6001\u6E32\u67D3\u7684\u7F3A\u70B9\u4E4B\u4E00\u662F\u5FC5\u987B\u4E3A\u6BCF\u4E2A\u53EF\u80FD\u7684URL\u751F\u6210\u5355\u72EC\u7684HTML\u6587\u4EF6\u3002\u5982\u679C\u65E0\u6CD5\u63D0\u524D\u9884\u6D4B\u8FD9\u4E9BURL\u7684\u5185\u5BB9\uFF0C\u6709\u53EF\u80FD\u5C31\u65E0\u6CD5\u901A\u8FC7\u9759\u6001\u6E32\u67D3\u5B9E\u73B0\u3002\u8FD9\u91CC\u8BF4\u4E00\u4E0B\u9759\u6001\u6E32\u67D3\u548C\u9884\u6E32\u67D3\u4E4B\u95F4\u7684\u533A\u522B\uFF1A\u9759\u6001\u6E32\u67D3\u662F\u4EA4\u4E92\u5F0F\u7684\uFF0C\u65E0\u9700\u6267\u884C\u5927\u91CF\u5BA2\u6237\u7AEFJS\uFF0C\u800C\u9884\u6E32\u67D3\u6539\u8FDB\u4E86\u5FC5\u987B\u542F\u52A8\u7684 <code>SPA\uFF08\u5355\u9875\u5E94\u7528\u7A0B\u5E8F\uFF09</code> \u7684\u9996\u6B21\u7ED8\u5236\u6216\u8005\u9996\u6B21\u7ED8\u5236\u5185\u5BB9\u4EE5\u4F7F\u9875\u9762\u771F\u6B63\u5177\u6709\u4EA4\u4E92\u6027\u3002<em>\u8FD9\u4E2A\u53EF\u4EE5\u901A\u8FC7\u770B\u535A\u5BA2\u6E32\u67D3\u5F15\u64CE\uFF08\u7C7B\u4F3Cjekyll\u7B49\uFF09\u548CSPA\u5E94\u7528\u7684\u533A\u522B\u4F1A\u6709\u6240\u7406\u89E3\u3002</em></p><p>\u5982\u679C\u4E0D\u786E\u5B9A\u662F\u8981\u7528\u9759\u6001\u6E32\u67D3\u8FD8\u662F\u9884\u6E32\u67D3\uFF0C\u53EF\u4EE5\u8FD9\u6837\u6D4B\u8BD5\u4E00\u4E0B\uFF1A\u7981\u7528JavaScript\u5E76\u52A0\u8F7D\u521B\u5EFA\u7684\u7F51\u9875\u3002\u5BF9\u4E8E\u9759\u6001\u6E32\u67D3\u7684\u9875\u9762\uFF0C\u5927\u591A\u6570\u529F\u80FD\u5728\u6CA1\u6709\u542F\u7528JavaScript\u7684\u60C5\u51B5\u4E0B\u4F9D\u7136\u53EF\u7528\u3002\u800C\u5BF9\u4E8E\u9884\u6E32\u67D3\u9875\u9762\uFF0C\u867D\u7136\u4ECD\u7136\u6709\u4E00\u4E9B\u57FA\u672C\u529F\u80FD\uFF0C\u6BD4\u5982\u94FE\u63A5\uFF0C\u4F46\u662F\u5927\u591A\u6570\u9875\u9762\u662F\u4E0D\u53EF\u7528 \u7684\u3002</p><p>\u8FD8\u53EF\u4EE5\u4F7F\u7528\u63A7\u5236\u53F0\u7F51\u7EDC\u677F\u5757\uFF0C\u964D\u4F4E\u7F51\u7EDC\u901F\u5EA6\uFF0C\u5E76\u89C2\u5BDF\u5728\u9875\u9762\u53D8\u4E3A\u53EF\u4EA4\u4E92\u4E4B\u524D\u4E0B\u8F7D\u4E86\u591A\u5C11JavaScript\u3002\u9884\u6E32\u67D3\u901A\u5E38\u9700\u8981\u66F4\u591A\u7684JavaScript\u624D\u53EF\u4EE5\u4EA4\u4E92\uFF0C\u800C\u4E14 JavaScript \u5F80\u5F80\u6BD4\u9759\u6001\u6E32\u67D3\u4F7F\u7528\u7684<a href="https://developer.mozilla.org/zh-CN/docs/Glossary/Progressive_Enhancement" target="_blank" rel="noreferrer">\u6E10\u8FDB\u589E\u5F3A</a>\u65B9\u6CD5\u66F4\u590D\u6742\u3002</p><h2 id="\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3" tabindex="-1">\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3 <a class="header-anchor" href="#\u670D\u52A1\u5668\u6E32\u67D3\u548C\u9759\u6001\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u670D\u52A1\u5668\u6E32\u67D3\u7684\u52A8\u6001\u7279\u6027\u4F1A\u5E26\u6765\u5927\u91CF\u7684 <a href="https://medium.com/airbnb-engineering/operationalizing-node-js-for-server-side-rendering-c5ba718acfc9" target="_blank" rel="noreferrer">\u8BA1\u7B97\u5F00\u9500</a>\u3002\u8BB8\u591A\u670D\u52A1\u5668\u6E32\u67D3\u65B9\u6848\u4E0D\u4F1A\u63D0\u524D\u5237\u65B0\uFF0C\u53EF\u80FD\u4F1A\u5EF6\u8FDF TTFB \u6216\u8005\u5C06\u53D1\u9001\u5230\u6570\u636E\u52A0\u500D\u3002\u5728React\u4E2D\uFF0C<code>renderToString()</code> \u53EF\u80FD\u5F88\u6162\uFF0C\u56E0\u4E3A\u5B83\u662F\u540C\u6B65\u548C\u5355\u7EBF\u7A0B\u7684\u3002\u670D\u52A1\u5668\u6E32\u67D3\u6B63\u786E\u7684\u4F7F\u7528\uFF0C\u53EF\u80FD\u79BB\u4E0D\u5F00\u7EC4\u4EF6\u7F13\u5B58\u3001\u5185\u5B58\u6D88\u8017\u3001<a href="https://speakerdeck.com/maxnajim/hastening-react-ssr-with-component-memoization-and-templatization" target="_blank" rel="noreferrer">\u5E94\u7528\u8BB0\u5FC6\u6280\u672F</a>\u7B49\u5176\u4ED6\u95EE\u9898\uFF0C\u4EC5\u4EC5\u56E0\u4E3A\u670D\u52A1\u5668\u6E32\u67D3\u53EF\u4EE5\u4F7F\u67D0\u4E9B\u5185\u5BB9\u66F4\u5FEB\u7684\u663E\u793A\u51FA\u6765\uFF0C\u5E76\u4E0D\u4EE3\u8868\u6240\u505A\u7684\u5DE5\u4F5C\u5C11\u3002</p><p>\u670D\u52A1\u5668\u6E32\u67D3\u4E3A\u6BCF\u4E2AURL\u6309\u9700\u751F\u6210HTML\uFF0C\u4F46\u53EF\u80FD\u4F1A\u6BD4\u4EC5\u63D0\u4F9B\u9759\u6001\u6E32\u67D3\u7684\u5185\u5BB9\u6162\u3002\u5982\u679C\u53EF\u4EE5\u7684\u8BDD\uFF0C\u670D\u52A1\u5668\u6E32\u67D3 <strong>+</strong> <a href="https://freecontent.manning.com/caching-in-react/" target="_blank" rel="noreferrer">HTML\u7F13\u5B58</a>\u53EF\u4EE5\u5927\u5927\u51CF\u5C11\u670D\u52A1\u5668\u6E32\u67D3\u65F6\u95F4\u3002\u4E0E\u9759\u6001\u6E32\u67D3\u76F8\u6BD4\uFF0C\u670D\u52A1\u5668\u6E32\u67D3\u7684\u4F18\u52BF\u5728\u4E8E\u53EF\u4EE5\u63D0\u53D6\u66F4\u591A\u201C\u52A8\u6001\u201D\u6570\u636E\u5E76\u54CD\u5E94\u5B8C\u6574\u7684\u8BF7\u6C42\u3002\u9700\u8981\u4E2A\u6027\u5316\u7684\u9875\u9762\u662F\u4E0D\u662F\u548C\u9759\u6001\u6E32\u67D3\u7684\u3002</p><h2 id="\u5BA2\u6237\u7AEF\u6E32\u67D3" tabindex="-1">\u5BA2\u6237\u7AEF\u6E32\u67D3 <a class="header-anchor" href="#\u5BA2\u6237\u7AEF\u6E32\u67D3" aria-hidden="true">#</a></h2><p><em>\u5BA2\u6237\u7AEF\u6E32\u67D3\u662F\u6307\u7528JavaScript\u76F4\u63A5\u5728\u6D4F\u89C8\u5668\u91CC\u6E32\u67D3\u9875\u9762\u3002\u6240\u6709\u7684\u903B\u8F91\u3001\u83B7\u53D6\u6570\u636E\u3001\u6A21\u677F\u3001\u8DEF\u7531\u90FD\u5728\u5BA2\u6237\u7AEF\u5904\u7406\u3002</em></p><p>\u5BF9\u4E8E\u79FB\u52A8\u8BBE\u5907\uFF0C\u5BA2\u6237\u7AEF\u6E32\u67D3\u53EF\u80FD\u5F88\u96BE\u4F7F\u7528\u5E76\u4E14\u4E00\u76F4\u80FD\u5FEB\u901F\u6267\u884C\u3002\u5982\u679C\u53EF\u4EE5\u505A\u6700\u5C11\u7684\u5DE5\u4F5C\uFF0C\u4FDD\u6301\u6700\u5C11\u7684JavaScript\u6027\u80FD\u9884\u7B97\u5E76\u5C3D\u53EF\u80FD\u51CF\u5C11 <a href="https://blog.csdn.net/qq_38890412/article/details/106663674" target="_blank" rel="noreferrer">\u5F80\u8FD4\u65F6\u5EF6\uFF08RTT\uFF09</a>\uFF0C \u5B83\u5C31\u53EF\u4EE5\u6709\u63A5\u8FD1\u7EAF\u670D\u52A1\u5668\u6E32\u67D3\u7684\u6027\u80FD\u3002\u53EF\u4EE5\u4F7F\u7528http2\uFF0C\u6216\u8005 <code>&lt;link rel=preload&gt;</code>\uFF0C\u8FD9\u6837\u53EF\u4EE5\u8BA9\u89E3\u6790\u5668\u66F4\u5FEB\u7684\u5DE5\u4F5C\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213163255.png" alt=""></p><p>\u5BA2\u6237\u7AEF\u6E32\u67D3\u7684\u4E3B\u8981\u7F3A\u70B9\u662F\u6240\u9700\u7684 JavaScript \u91CF\u5F80\u5F80\u4F1A\u968F\u7740\u5E94\u7528\u7A0B\u5E8F\u7684\u589E\u957F\u800C\u589E\u957F\uFF0C\u6BD4\u5982\u6DFB\u52A0\u4E86\u65B0\u7684\u5E93\u3001polyfill\u3001\u4E09\u65B9\u4EE3\u7801\u7B49\u3002\u5B83\u4EEC\u4F1A\u4E89\u593A\u9875\u9762\u7684\u5904\u7406\u987A\u5E8F\uFF0C\u5E76\u4E14\u901A\u5E38\u5FC5\u987B\u5728\u6E32\u67D3\u9875\u9762\u5185\u5BB9\u4E4B\u524D\u5904\u7406\u3002\u6240\u4EE5\u5728\u4F7F\u7528\u4F9D\u8D56\u5927\u578BJavaScript\u5305\u7684CSR\u6784\u5EFA\u65F6\uFF0C\u5E94\u8BE5\u8003\u8651\u4EE3\u7801\u62C6\u5206\uFF0C\u5E76\u786E\u4FDD\u5EF6\u8FDF\u52A0\u8F7DJavaScript\uFF0C\u4E5F\u5C31\u662F\u6309\u9700\u52A0\u8F7D\uFF1A\u53EA\u5728\u9700\u8981\u65F6\u63D0\u4F9B\u9700\u8981\u7684\u670D\u52A1\u3002</p><h2 id="rehydration\u4E0B\u7684ssr\u548Ccsr" tabindex="-1">rehydration\u4E0B\u7684SSR\u548CCSR <a class="header-anchor" href="#rehydration\u4E0B\u7684ssr\u548Ccsr" aria-hidden="true">#</a></h2><p>hydration\u76F4\u8BD1\u4E3A\u6C34\u5408\uFF0C\u53EF\u4EE5\u7406\u89E3\u4E3A\u5BF9\u66FE\u7ECF\u6E32\u67D3\u8FC7\u7684HTML\u8FDB\u884C\u91CD\u65B0\u6E32\u67D3\u7684\u8FC7\u7A0B\uFF0Crehydration\u53EF\u4EE5\u7406\u89E3\u4E3A\u4E00\u79CD\u4F7F\u7528\u6C34\u5408\u7684\u6280\u672F\uFF0C\u4E5F\u88AB\u53EB\u505A\u540C\u6784\u6E32\u67D3\u3002</p><p>\u8BE5\u65B9\u6CD5\u4F1A\u540C\u65F6\u8FDB\u884C\u670D\u52A1\u5668\u6E32\u67D3\u548C\u5BA2\u6237\u7AEF\u6E32\u67D3\uFF0C\u4EE5\u6743\u8861\u6E32\u67D3\u5E73\u8861\u3002\u8FD9\u6837\u7684\u9996\u6B21\u5185\u5BB9\u7ED8\u5236\u4F1A\u5F88\u5FEB\uFF0C\u5C31\u50CF\u670D\u52A1\u5668\u6E32\u67D3\u4E00\u6837\uFF0C\u7136\u540E\u7528\u4E00\u4E2A\u540C\u6784\u6280\u672F\uFF08rehydration\uFF09\u5728\u5BA2\u6237\u7AEF\u518D\u6B21\u6E32\u67D3\u3002</p><p>\u8BE5\u6280\u672F\u4E3B\u8981\u7684\u7F3A\u70B9\u5C31\u662F\uFF0C\u6267\u884CJS\u4E4B\u524D\uFF0C\u4EC0\u4E48\u4E5F\u505A\u4E0D\u4E86\uFF0C\u4E0B\u9762\u662F\u4E00\u4E2A\u4F8B\u5B50\uFF1A</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213165856.png" alt=""></p><p>\u670D\u52A1\u5668\u8FD4\u56DE\u9875\u9762\u8282\u70B9\uFF0C\u4E5F\u5C31\u662F\u4E09\u4E2Acheckbox\uFF0C\u4E5F\u4F1A\u8FD4\u56DEDATA\uFF0C\u518D\u52A0\u8F7DJS\u811A\u672C\uFF0C\u53EA\u6709 bundle.js \u5B8C\u6210\u52A0\u8F7D\u548C\u6267\u884C\u4E4B\u540E\uFF0C\u9875\u9762\u624D\u53D8\u6210\u4EA4\u4E92\u5F0F\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213163143.png" alt=""></p><h2 id="\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784" tabindex="-1">\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784 <a class="header-anchor" href="#\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u548C\u6E10\u8FDB\u5F0F\u540C\u6784" aria-hidden="true">#</a></h2><p>\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u5141\u8BB8\u4EE5\u5757\u7684\u5F62\u5F0F\u53D1\u9001HTML\uFF0C\u6D4F\u89C8\u5668\u63A5\u6536\u5230\u4E4B\u540E\u628A\u4ED6\u4EEC\u62FC\u63A5\u6E32\u67D3\u3002\u8FD9\u6837\u53EF\u4EE5\u6709\u5FEB\u901F\u7684\u9996\u6B21\u7ED8\u5236\u548C\u9996\u6B21\u5185\u5BB9\u7ED8\u5236\u3002</p><p>\u4F7F\u7528\u6E10\u8FDB\u5F0F\u540C\u6784\u7684\u8BDD\uFF0C\u670D\u52A1\u5668\u6E32\u67D3\u7684\u7A0B\u5E8F\u7684\u5404\u4E2A\u90E8\u5206\u4F1A\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\u800C\u6267\u884C\uFF0C\u800C\u4E0D\u662F\u5F53\u524D\u4E00\u6B21\u5C31\u521D\u59CB\u5316\u6574\u4E2A\u7A0B\u5E8F\u3002\u8FD9\u6837\u53EF\u4EE5\u63A8\u8FDF\u9875\u9762\u4F4E\u4F18\u5148\u7EA7\u90E8\u5206\u7684\u4EE3\u7801\u4EE5\u9632\u6B62\u963B\u585E\u4E3B\u7EBF\u7A0B\u3002</p><h2 id="\u4E09\u4F53\u6E32\u67D3" tabindex="-1">\u4E09\u4F53\u6E32\u67D3 <a class="header-anchor" href="#\u4E09\u4F53\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u7ED3\u5408 Service Worker \u7684\u8BDD\uFF0C\u4E09\u9898\u6E32\u67D3\u6280\u672F\u4E5F\u53EF\u4EE5\u4F7F\u7528\u3002\u53EF\u4EE5\u4F7F\u7528\u6D41\u5F0F\u670D\u52A1\u5668\u6E32\u67D3\u8FDB\u884C\u521D\u59CB\u5BFC\u822A\uFF0C\u7136\u540E\u8BA9 Service Worker \u5B89\u88C5\u540E\u4E3A\u5BFC\u822A\u6E32\u67D3HTML\u3002\u8FD9\u53EF\u4EE5\u4F7F\u7F13\u5B58\u7684\u7EC4\u4EF6\u548C\u6A21\u677F\u4FDD\u6301\u6700\u65B0\uFF0C\u5E76\u542F\u7528 SPA \u6837\u5F0F\u7684\u5BFC\u822A\u4EE5\u5728\u540C\u4E00\u4F1A\u8BDD\u4E2D\u6E32\u67D3\u65B0\u89C6\u56FE\u3002\u5982\u679C\u53EF\u4EE5\u5728\u670D\u52A1\u5668\u3001\u5BA2\u6237\u7AEF\u9875\u9762\u548C Service Worker \u4E4B\u95F4\u5171\u4EAB\u76F8\u540C\u7684\u6A21\u677F\u548C\u8DEF\u7531\u4EE3\u7801\uFF0C\u4E09\u4F53\u6E32\u67D3\u662F\u4E2A\u4E0D\u9519\u7684\u9009\u62E9\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213165517.png" alt=""></p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u4E0B\u56FE\u4E3A\u670D\u52A1\u5668-\u5BA2\u6237\u7AEF\u6E32\u67D3\u5230\u8303\u56F4\uFF0C\u53EF\u4EE5\u53C2\u7167\u4E0B\u56FE\u6765\u51B3\u5B9A\u4F7F\u7528\u54EA\u79CD\u6E32\u67D3\u65B9\u5F0F\uFF1A</p><p><img src="https://cdn.jsdelivr.net/gh/daodaolee/photobed@main/img/20211213165749.png" alt=""></p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1">\u53C2\u8003\u8D44\u6599 <a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a></h2><ul><li><a href="https://developers.google.com/web/updates/2019/02/rendering-on-the-web" target="_blank" rel="noreferrer">Rendering on the Web </a></li><li><a href="https://zhuanlan.zhihu.com/p/62766037" target="_blank" rel="noreferrer">\u4EC0\u4E48\u662FCPU\u5BC6\u96C6\u578B\u3001IO\u5BC6\u96C6\u578B\uFF1F</a></li><li><a href="https://juejin.cn/post/6844903700524171271" target="_blank" rel="noreferrer">\u5F00\u542F\u6027\u80FD\u9884\u7B97</a></li><li><a href="https://blog.csdn.net/qq_38890412/article/details/106663674" target="_blank" rel="noreferrer">\u50BB\u5B50\u90FD\u80FD\u770B\u61C2\u7684\u2014\u2014RTT</a></li><li><a href="https://ssr.vuejs.org/zh/guide/streaming.html#%E6%B5%81%E5%BC%8F%E4%BC%A0%E8%BE%93%E8%AF%B4%E6%98%8E-streaming-caveats" target="_blank" rel="noreferrer">Vue SSR \u6D41\u5F0F\u6E32\u67D3</a></li><li><a href="https://www.zhihu.com/question/379831174" target="_blank" rel="noreferrer">\u524D\u7AEF\u540C\u6784\u5E94\u7528\u548CSSR\u6709\u4EC0\u4E48\u533A\u522B</a></li><li><a href="https://developer.mozilla.org/zh-CN/docs/Glossary/Progressive_Enhancement" target="_blank" rel="noreferrer">\u6E10\u8FDB\u589E\u5F3A</a></li></ul>',48),n=[l];function o(h,d,s,c,p,g){return r(),a("div",null,n)}const u=e(i,[["render",o]]);export{m as __pageData,u as default};
